import React, { useState, useEffect } from 'react';
import { Container, Card, Button, Form } from 'react-bootstrap';

const TravelDiary = () => {
  const [diaries, setDiaries] = useState([]);
  const [newDiary, setNewDiary] = useState({
    title: '',
    content: '',
    date: '',
    location: '',
    images: []
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: 实现日记保存到后端的逻辑
    setDiaries([...diaries, newDiary]);
    setNewDiary({ title: '', content: '', date: '', location: '', images: [] });
  };

  return (
    <Container className="py-4">
      <h2 className="mb-4">我的出行日记</h2>

      {/* 新建日记表单 */}
      <Card className="mb-4">
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>标题</Form.Label>
              <Form.Control
                type="text"
                value={newDiary.title}
                onChange={(e) => setNewDiary({...newDiary, title: e.target.value})}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>地点</Form.Label>
              <Form.Control
                type="text"
                value={newDiary.location}
                onChange={(e) => setNewDiary({...newDiary, location: e.target.value})}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>日期</Form.Label>
              <Form.Control
                type="date"
                value={newDiary.date}
                onChange={(e) => setNewDiary({...newDiary, date: e.target.value})}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>内容</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={newDiary.content}
                onChange={(e) => setNewDiary({...newDiary, content: e.target.value})}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              保存日记
            </Button>
          </Form>
        </Card.Body>
      </Card>

      {/* 日记列表 */}
      {diaries.map((diary, index) => (
        <Card key={index} className="mb-3">
          <Card.Body>
            <Card.Title>{diary.title}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">
              {diary.location} - {diary.date}
            </Card.Subtitle>
            <Card.Text>{diary.content}</Card.Text>
          </Card.Body>
        </Card>
      ))}
    </Container>
  );
};

export default TravelDiary;
