import React, { useState, useEffect } from 'react';
import { Container, Card, Badge } from 'react-bootstrap';

const MyOrders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // TODO: 从后端获取订单数据
    const fetchOrders = async () => {
      // 模拟数据
      const mockOrders = [
        {
          id: '1',
          tourName: '东京三日游',
          orderDate: '2024-03-20',
          status: 'pending',
          price: 3999,
          participants: 2
        },
        // 更多订单...
      ];
      setOrders(mockOrders);
    };

    fetchOrders();
  }, []);

  const getStatusBadge = (status) => {
    const statusMap = {
      pending: { text: '待支付', variant: 'warning' },
      paid: { text: '已支付', variant: 'success' },
      cancelled: { text: '已取消', variant: 'danger' },
      completed: { text: '已完成', variant: 'info' }
    };

    const statusInfo = statusMap[status] || { text: '未知', variant: 'secondary' };
    return <Badge bg={statusInfo.variant}>{statusInfo.text}</Badge>;
  };

  return (
    <Container className="py-4">
      <h2 className="mb-4">我的订单</h2>

      {orders.map((order) => (
        <Card key={order.id} className="mb-3">
          <Card.Body>
            <div className="d-flex justify-content-between align-items-start">
              <div>
                <Card.Title>{order.tourName}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  订单号: {order.id}
                </Card.Subtitle>
              </div>
              {getStatusBadge(order.status)}
            </div>
            <Card.Text>
              <div>下单日期: {order.orderDate}</div>
              <div>参与人数: {order.participants}人</div>
              <div>订单金额: ¥{order.price}</div>
            </Card.Text>
          </Card.Body>
        </Card>
      ))}
    </Container>
  );
};

export default MyOrders;
